mapper.delete:
update clreply
set content=#{content}
WHERE clrID=#{clrID} and clID=#{clID}

mapper.select:
	select clrID, clID, content, rdate, r 
		from(
		 select clrID, clID, content, rdate, rownum r 
		 from(
			select clrID, clID, content, rdate from clreply
			where clID=#{clID})

         )where r >= #{sno} and r<= #{eno}
