mapper.update:
update club
title = #{title}, content = #{content}, rdate = sysdate
where clID = #{clID}

update club
set fileName =
#{fileName}
where clID = #{clID}

update club
set viewcnt = viewcnt + 1
where clID = #{clID}

mapper.select:
		SELECT clID, title, content, nickname, viewcnt, rdate, ID, fileName,
		regionID, r
		from ( SELECT clID, title, content, nickname, viewcnt, rdate, ID,
		fileName, regionID, rownum r
		from ( SELECT clID, title, content, nickname, viewcnt, rdate, ID,
		fileName, regionID
		FROM club



					nickname like '%'||#{word}||'%'


					title like '%'||#{word}||'%'


					content like '%'||#{word}||'%'


					title like '%'||#{word}||'%'
					or
					content like '%'||#{word}||'%'


			and regionID = #{regionID}

		ORDER BY clID DESC
		)

         )where r >= #{sno} and r <= #{eno}
