mapper.select:
		select cId, title, content, viewcnt, rdate, id, category, nickname, r
		from (
			select cId, title,  content, viewcnt, rdate, id, category, nickname, rownum r
			from(
				select cId, title, content, viewcnt, rdate, id, category, nickname
				from community



						title like '%'||#{word}||'%'


						nickname like '%'||#{word}||'%'


						content like '%'||#{word}||'%'


						category like '%'||#{word}||'%'




		       order by cId desc
			)

         )where r >= #{sno} and r <= #{eno}

mapper.update:
update community
set title = #{title}, 
content = #{content},
category = #{category}
where cid = #{cid}

mapper.select:
select cId, title, content, nickname, viewcnt, rdate, category, ID
from community
where cid = #{cid}

mapper.update:
update community
set viewcnt = viewcnt + 1
where cid = #{cid}
