mapper.select:
SELECT COUNT(*) AS cnt
FROM fboard
WHERE fbID=#{fbID} AND password=#{password}

mapper.update:
update fboard
set title=#{title}, content=#{content}, userID=#{userID}
where fbID =#{fbID}

mapper.select:
select fbID, title, content, password, viewcnt, rdate, userID, nickname, category
from fboard
where fbID = #{fbID}

mapper.update:
update fboard
set viewcnt = viewcnt + 1
where fbID = #{fbID}

mapper.select:
		select fbID, title, content, password, viewcnt, rdate, userID, nickname, category, regionID, r
		from (
			select fbID, title, content, password, viewcnt, rdate, userID, nickname, category, regionID, rownum r
			from(
				select fbID, title, content, password, viewcnt, rdate, userID, nickname, category, regionID
				from fboard



						title like '%'||#{word}||'%'


						content like '%'||#{word}||'%'


				and regionID = #{regionID}

		       order by fbID desc
			)

         )where r >= #{sno} and r <= #{eno}
