mapper.select:
SELECT COUNT(*) AS cnt
FROM notice
WHERE nID=#{nID} AND password=#{password}

mapper.update:
update notice
set title=#{title}, content=#{content}, ID=#{ID}
where nID =#{nID}

mapper.select:
select nID, title, content, password, viewcnt, rdate, ID
from notice
where nID = #{nID}

mapper.update:
update notice
set viewcnt = viewcnt + 1
where nID = #{nID}

mapper.select:
		select nID, title, content, password, viewcnt, rdate, ID, r
		from (
			select nID, title, content, password, viewcnt, rdate, ID, rownum r
			from(
				select nID, title, content, password, viewcnt, rdate, ID
				from notice



						title like '%'||#{word}||'%'


						content like '%'||#{word}||'%'



		       order by nID desc
			)

         )where r >= #{sno} and r <= #{eno}
