mapper.select:
select tid, title, districtcode, writer, nickname, score, rdate, contents, filename
from tour
where tid = #{tid}

mapper.update:
update tour
set filename = #{filename}
where tid = #{tid}

mapper.select:
		select tid, title, districtcode, score, writer, nickname, rdate, filename, r
		from (
			select tid, title, districtcode, score, writer, nickname, rdate, filename, rownum r
			from(
				select tid, title, districtcode, score, writer, nickname, rdate, filename
				from tour



						title like '%'||#{word}||'%'



						writer like '%'||#{word}||'%'


				and districtcode = #{districtcode}

		       order by tID desc
			)

         )where r >= #{sno} and r <= #{eno}

mapper.update:
update tour
set viewcnt = viewcnt + 1
where tid = #{tid}
