mapper.select:
select trid, content, rdate, id, tid, r
FROM(
        select trid, content, rdate, id, tid, rownum r
        FROM(
                select trid, content, rdate, id, tid
                from TREPLY
                where tid = #{tid}
                order by rnum DESC
           )

		)WHERE r >= #{sno} and r <= #{eno}
