mapper.delete:
update treply
set content=#{content}
WHERE trid=#{trid} and tid=#{tid}

mapper.select:
	select trid, tid, content, rdate, r 
		from(
		 select trid, tid, content, rdate, rownum r 
		 from(
			select trid, tid, content, rdate from treply
			where tid=#{tid})

         )where r >= #{sno} and r<= #{eno}
