mapper.select:
SELECT ID, uname, nickname, email, phone, address1, address2, gender, birth,
fileName, r
from(
	SELECT ID, uname, nickname, email, phone, address1, address2, gender, birth,
fileName, rownum r
	from(
		SELECT ID, uname, nickname, email, phone, address1, address2, gender, birth,
fileName
		FROM users



					uname like '%'||#{word}||'%'


					ID like '%'||#{word}||'%'


					email like '%'||#{word}||'%'



		ORDER BY rdate DESC
		)

     )where r >= #{sno} and r <= #{eno}

mapper.update:
update users
set fileName = #{fileName}
where ID = #{ID}

mapper.select:
select count(*) from users
where ID = #{ID}
and password = #{password}

SELECT COUNT(id)
FROM users
WHERE ID = #{ID}

SELECT COUNT(email)
FROM users
WHERE email=#{email}
